import React, { useState } from 'react';
import axios from 'axios';

export default function Quiz() {
  const [file, setFile] = useState(null);
  const [numberOfQuizzes, setNumberOfQuizzes] = useState(0);
  const [quizQuestions, setQuizQuestions] = useState([]);

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleNumberChange = (event) => {
    setNumberOfQuizzes(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!file) {
      alert('Please upload a file.');
      return;
    }

    const formData = new FormData();
    formData.append('file', file);

    try {
      // Upload the file to the server to get custom data
      const response = await fetch('http://localhost:8000/api/custom-data', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ data: customData })
          });
          const data = await response.json();

      if (uploadResponse.data.message === 'Custom data uploaded successfully.') {
        // Fetch quiz questions using the custom data
        const quizResponse = await axios.post('http://localhost:8000/api/quiz', {
          numberOfQuestions: numberOfQuizzes
        });

        setQuizQuestions(quizResponse.data.questions);
      } else {
        alert('Error uploading file.');
      }
    } catch (error) {
      console.error('Error:', error);
      alert('An error occurred while processing your request.');
    }
  };

  return (
    <div className="flex items-center justify-center h-screen bg-gray-100">
      <div className="bg-white p-8 rounded shadow-md w-full max-w-md">
        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="fileUpload">
              Upload File
            </label>
            <input
              type="file"
              id="fileUpload"
              className="border border-gray-300 p-2 rounded w-full"
              onChange={handleFileChange}
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="quizNumber">
              Number of quizzes to generate
            </label>
            <input
              type="number"
              id="quizNumber"
              className="border border-gray-300 p-2 rounded w-full"
              placeholder="Enter number"
              value={numberOfQuizzes}
              onChange={handleNumberChange}
            />
          </div>
          <div>
            <button type="submit" className="bg-blue-500 text-white p-2 rounded w-full hover:bg-blue-700">
              Submit
            </button>
          </div>
        </form>
        {quizQuestions.length > 0 && (
          <div className="mt-4">
            <h2 className="text-xl font-bold mb-4">Generated Quiz Questions</h2>
            {quizQuestions.map((quiz, index) => (
              <div key={index} className="mb-4">
                <p className="font-bold">{quiz.question}</p>
                <ul className="list-disc pl-5">
                  {quiz.options.map((option, i) => (
                    <li key={i}>{option}</li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}
