import React, { useState } from 'react';

const ChatBox = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [fileUploaded, setFileUploaded] = useState(false);

  const handleSend = () => {
    if (input.trim()) {
      setMessages([...messages, { user: 1, text: input }]);
      setInput('');
    }
  };

  const handleFileUpload = (event) => {
    setFileUploaded(true);
  };

  return (
    <div className="flex justify-center items-center h-screen bg-gray-100">
      <div className="bg-white shadow-lg rounded-lg w-full max-w-md p-4 flex flex-col space-y-4">
        {!fileUploaded && (
          <div className="flex justify-center items-center h-48 border-dashed border-2 border-gray-300">
            <input type="file" onChange={handleFileUpload} />
          </div>
        )}
        {fileUploaded && (
          <div className="flex flex-col space-y-4 overflow-y-auto h-80">
            {messages.map((message, index) => (
              <div
                key={index}
                className={`flex ${message.user === 1 ? 'justify-end' : 'justify-start'}`}
              >
                <div
                  className={`p-2 rounded-lg ${
                    message.user === 1 ? 'bg-blue-500 text-white' : 'bg-gray-200 text-gray-800'
                  }`}
                >
                  {message.text}
                </div>
              </div>
            ))}
          </div>
        )}
        <div className="flex space-x-2">
          <input
            type="text"
            className="flex-1 p-2 border rounded-full"
            value={input}
            onChange={(e) => setInput(e.target.value)}
          />
          <button
            className="bg-blue-500 text-white p-2 rounded-full"
            onClick={handleSend}
          >
            Send
          </button>
        </div>
      </div>
    </div>
  );
};

export default ChatBox;
