import React, { useState } from 'react'

export default function Home() {
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState('');
    const [customData, setCustomData] = useState('');

    const sendMessage = async () => {
        const response = await fetch('http://localhost:8000/api/message', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ message: input })
        });
        const data = await response.json();
        setMessages([...messages, { user: input, bot: data.message }]);
        setInput('');
    };

    const sendCustomData = async () => {
        const response = await fetch('http://localhost:8000/api/custom-data', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ data: customData })
        });
        const data = await response.json();
        alert(data.message);
    };

    return (
        <div className="App">
            <div>
                <textarea
                    value={customData}
                    onChange={(e) => setCustomData(e.target.value)}
                    placeholder="Enter custom data here"
                    rows="10"
                    cols="50"
                />
                <button onClick={sendCustomData}>Send Custom Data</button>
            </div>
            <div className="chatbox">
                {messages.map((msg, index) => (
                    <div key={index}>
                        <div><b>User:</b> {msg.user}</div>
                        <div><b>Bot:</b> {msg.bot}</div>
                    </div>
                ))}
            </div>
            <div class="bg-white p-4 rounded-lg">
                <div class="relative bg-inherit">
                    <input value={input} onChange={(e) => setInput(e.target.value)} type="text" id="username" name="username" class="peer bg-transparent h-10 w-72 rounded-lg text-gray-200 placeholder-transparent ring-2 px-2 ring-gray-500 focus:ring-sky-600 focus:outline-none focus:border-rose-600" placeholder="Type inside me" /><label for="username" class="absolute cursor-text left-0 -top-3 text-sm text-gray-500 bg-inherit mx-1 px-1 peer-placeholder-shown:text-base peer-placeholder-shown:text-gray-500 peer-placeholder-shown:top-2 peer-focus:-top-3 peer-focus:text-sky-600 peer-focus:text-sm transition-all">Type inside me</label>
                </div>
            </div>

            <button onClick={sendMessage}>Send</button>
        </div>
    );
}
